contract BridgeTokenManager is Ownable, IBridgeTokenManager {
    mapping(address => IBridgeTokenManager.Token) internal _store;

    event TokenAdded(Token token);
    event TokenRemoved(Token token);
    event RootUpdated(address indexed prev, address indexed current);

    /**
     * @dev This should be responsible to connect tokens between chains
