     * @param receiveToken token address on chain receive
     */
    function issue(
        address exitToken,
        address receiveToken,
        IBridgeTokenManager.IssueType issueType
    ) external onlyOwner {
        require(!_store[exitToken].exist, "BridgeTokenManager: EXIST");

        Token memory token = Token(exitToken, receiveToken, true, issueType);
        _store[exitToken] = token;
        emit TokenAdded(token);
    }

    /**
     * @dev This should be responsible to remove tokens connection between chains
     * @param exitToken token address on exit calldata
     */
    function revoke(address exitToken) external onlyOwner {
        require(_store[exitToken].exist, "BridgeTokenManager: NOT_EXIST");

        Token memory token = _store[exitToken];
        delete _store[exitToken];
        emit TokenRemoved(token);
    }

    /**
     * @dev This should be responsible to get token mapping on current chain
     * @param exitToken token address on exit calldata
     */
    function get(address exitToken)
        external
        view
        override
        returns (IBridgeTokenManager.Token memory receiveToken)
    {
        return _store[exitToken];
    }

    function isRootToken(address token) external pure override returns (bool) {
        return address(0) == token;
    }
}

